import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [walletConnected, setWalletConnected] = useState(false);
  const [walletAddress, setWalletAddress] = useState('');
  const [currentSession, setCurrentSession] = useState(null);
  const [signatureRequest, setSignatureRequest] = useState(null);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // 模拟API调用
  const callAgentAPI = async (message) => {
    // 模拟API调用延迟
    await new Promise(resolve => setTimeout(resolve, 1000));

    // 模拟工作流执行
    const sessionId = `session_${Date.now()}`;
    const workflowId = `workflow_${Date.now()}`;

    return {
      session_id: sessionId,
      workflow_id: workflowId,
      status: 'pending',
      message: '工作流已提交，正在处理中...'
    };
  };

  const pollWorkflowStatus = async (sessionId) => {
    // 模拟轮询
    await new Promise(resolve => setTimeout(resolve, 2000));

    // 模拟需要签名
    return {
      session_id: sessionId,
      status: 'waiting_signature',
      need_signature: true,
      signature_request: {
        type: 'transaction_signature',
        action: 'swap',
        from_token: 'USDT',
        to_token: 'BTC',
        amount: '1000',
        gas_fee: '0.001 ETH',
        slippage: '0.5%'
      }
    };
  };

  const submitSignature = async (sessionId, signature) => {
    // 模拟签名提交
    await new Promise(resolve => setTimeout(resolve, 1000));

    return {
      session_id: sessionId,
      status: 'processing',
      message: '签名已提交，正在处理...'
    };
  };

  const connectWallet = async () => {
    // 模拟钱包连接
    await new Promise(resolve => setTimeout(resolve, 1000));

    const accounts = [
      '0x742d35Cc6634C0532925a3b8D4C9db96C4b4d8b6',
      '0x1234567890123456789012345678901234567890'
    ];

    setWalletConnected(true);
    setWalletAddress(accounts[0]);

    return {
      connected: true,
      accounts: accounts,
      network: 'Ethereum Mainnet',
      chain_id: '1'
    };
  };

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMessage = inputMessage;
    setInputMessage('');
    setIsLoading(true);

    // 添加用户消息
    setMessages(prev => [...prev, {
      id: Date.now(),
      type: 'user',
      content: userMessage,
      timestamp: new Date()
    }]);

    try {
      // 1. 调用智能体API
      const execution = await callAgentAPI(userMessage);
      setCurrentSession(execution);

      // 添加系统消息
      setMessages(prev => [...prev, {
        id: Date.now() + 1,
        type: 'system',
        content: '🔄 正在分析您的请求...',
        timestamp: new Date()
      }]);

      // 2. 轮询工作流状态
      const status = await pollWorkflowStatus(execution.session_id);

      if (status.need_signature) {
        setSignatureRequest(status.signature_request);
        setMessages(prev => [...prev, {
          id: Date.now() + 2,
          type: 'system',
          content: '✍️ 需要您签名授权交易，请点击下方按钮进行签名',
          timestamp: new Date(),
          requiresSignature: true
        }]);
      } else {
        setMessages(prev => [...prev, {
          id: Date.now() + 2,
          type: 'system',
          content: '✅ 工作流执行完成！',
          timestamp: new Date()
        }]);
      }

    } catch (error) {
      setMessages(prev => [...prev, {
        id: Date.now() + 3,
        type: 'error',
        content: `❌ 处理失败: ${error.message}`,
        timestamp: new Date()
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleSignature = async () => {
    if (!signatureRequest) return;

    setIsLoading(true);

    try {
      // 模拟签名过程
      const signature = `0x${Math.random().toString(16).substr(2, 64)}`;

      await submitSignature(currentSession.session_id, signature);

      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'system',
        content: '✅ 签名已提交，交易正在处理中...',
        timestamp: new Date()
      }]);

      // 模拟交易完成
      setTimeout(() => {
        setMessages(prev => [...prev, {
          id: Date.now() + 1,
          type: 'system',
          content: '🎉 交易完成！您的USDT已成功兑换为BTC',
          timestamp: new Date()
        }]);
        setSignatureRequest(null);
        setCurrentSession(null);
      }, 2000);

    } catch (error) {
      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'error',
        content: `❌ 签名失败: ${error.message}`,
        timestamp: new Date()
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  const handleConnectWallet = async () => {
    try {
      await connectWallet();
      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'system',
        content: `🔗 钱包连接成功！地址: ${walletAddress}`,
        timestamp: new Date()
      }]);
    } catch (error) {
      setMessages(prev => [...prev, {
        id: Date.now(),
        type: 'error',
        content: `❌ 钱包连接失败: ${error.message}`,
        timestamp: new Date()
      }]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  return (
    <div className="app">
      <div className="header">
        <h1>🤖 QNG 智能体</h1>
        <div className="wallet-info">
          {walletConnected ? (
            <span className="connected">
              🔗 {walletAddress.slice(0, 6)}...{walletAddress.slice(-4)}
            </span>
          ) : (
            <button 
              className="connect-btn"
              onClick={handleConnectWallet}
              disabled={isLoading}
            >
              🔗 连接钱包
            </button>
          )}
        </div>
      </div>

      <div className="chat-container">
        <div className="messages">
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.type}`}>
              <div className="message-content">
                {message.content}
              </div>
              <div className="message-time">
                {message.timestamp.toLocaleTimeString()}
              </div>
            </div>
          ))}
          {isLoading && (
            <div className="message system">
              <div className="message-content">
                <div className="loading">
                  <span>⏳</span>
                  <span>处理中</span>
                  <span>...</span>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {signatureRequest && (
          <div className="signature-request">
            <h3>✍️ 交易签名请求</h3>
            <div className="signature-details">
              <p><strong>操作:</strong> {signatureRequest.action}</p>
              <p><strong>从:</strong> {signatureRequest.from_token}</p>
              <p><strong>到:</strong> {signatureRequest.to_token}</p>
              <p><strong>数量:</strong> {signatureRequest.amount}</p>
              <p><strong>Gas费:</strong> {signatureRequest.gas_fee}</p>
              <p><strong>滑点:</strong> {signatureRequest.slippage}</p>
            </div>
            <button 
              className="signature-btn"
              onClick={handleSignature}
              disabled={isLoading}
            >
              🔐 确认签名
            </button>
          </div>
        )}

        <div className="input-container">
          <textarea
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="输入您的请求，例如：我需要将1000USDT兑换成BTC"
            disabled={isLoading}
          />
          <button 
            onClick={handleSendMessage}
            disabled={isLoading || !inputMessage.trim()}
            className="send-btn"
          >
            发送
          </button>
        </div>
      </div>
    </div>
